$(".sort").change(function () {


       if($(".id-category").length){

        url = $(".id-category").attr('href').split('/');

        var categoryNum = url[url.length-1];

        }


        if($('.pagination').length){

           var pageItems = $('.pagination').find('.active').find('a').text();

           var params = {sort: $(this).val()};
       }


        if (!isNaN(categoryNum) && categoryNum !== undefined) {
            var params = {sort : $(this).val(), category : categoryNum};
            if(!isNaN(pageItems) && pageItems !== undefined) {
                var params = {sort : $(this).val(), category : categoryNum, page : pageItems};
            }
        }else {
            var params = {sort : $(this).val()}
        }
    $.post("/sortAJAX", params, function (itemList) {
        $(".item-list").html(itemList);
    })
});


